int add(int a, int b) {
    return a + b;
    System.out.println("This never runs!"); // Compilation error
}

String classify(int grade) {
    if (grade >= 7) {
        return "Passed";
    } else {
        return "Failed";
    }
}

void check(int x) {
    if (x < 0) {
        return; // exits the method without returning a value
    }
    System.out.println("Value: " + x);
}

class Animal {
    String name;
    void makeSound() { }
}

class Dog extends Animal {
    void makeSound() {
        System.out.println("Woof!");
    }
}
